def asn_confirm(properties,login,asn_data):
    url = properties["twms_url"] + "/en/admin/asn/confirm/" + asn_data["asn_id"]
    headers = {
        'Content-Type': 'application/x-www-form-urlencoded',
      'X-CSRF-TOKEN': login['csrf_token'],
      'Cookie': 'XSRF-TOKEN=' + login['cookies']['XSRF-TOKEN'] + '; laravel_session='+login['cookies']['laravel_session']
    }
    payload={'_token': login['csrf_token'],
            'confirm_type': 'all',
            'number_of_carton': '0',
            'ata_at': str((datetime.datetime.now()).strftime('%Y-%m-%d')),
            'item': '10',
            'carton': '0',
            'pallet': '0',
            'item_ids[]': str(asn_data["item_id"])}
    response = requests.request("POST", url, headers=headers, data=payload)
    # print(response.text)
    if "Create ASN" in response.text:
        print("全部确认收货成功")
        print("")
    else:
        print(response.text)











